mapper.select:
SELECT user_id, user_name, category_name, group_name, shop_name, user_role, enabled FROM users
AND user_role = #{user_role}
ORDER BY reg_date desc limit #{startRowNum },#{pageSize}

mapper.update:
UPDATE users
password=#{password}, user_name=#{user_name}, category_name=#{category_name}, group_name=#{group_name}, shop_name=#{shop_name}, user_role=#{user_role}, enabled=#{enabled},
WHERE user_id = #{user_id};
